reviews = []

games.each do |game|
  revs = review_array.sample(5)
  use = users.sample(5)
  (0..4).each do |i|
    reviews << {user_id: use[i].id, game_id: game.id, body: revs[i][:body], title: revs[i][:title]}
  end
end


Review.create(reviews)

ratings = []

def rounder(num)
  if num < 0.2
    1
  elsif num < 0.3
    2
  elsif num < 0.45
    3
  elsif num < 0.75
    4
  else
    5
  end
end


games.each do |game|
  use = users.sample(5)
  use.each do |user|
    ratings << {user_id: user.id, game_id: game.id, num: rounder(rand())}
  end
end

Rating.create(ratings)
